import React, { Component } from 'react'
import './index.css'
export default class Footer extends Component {
  totalCheckedChange=(e)=>{
    console.log(e.target.checked);
  }
  render() {
    const {todos}=this.props
    const done=todos.reduce((pre,cur)=>{
      return pre+(cur.done===true?1:0)
    },0)
    return (
        <div className="todo-footer">
            <label>
                    {/*注意不能使用defaultChecked,这个只能在初始化的时候执行一次，并且如果使用checkede就必须添加onChange*/}
                <input type="checkbox" checked={done==todos.length?true:false} onChange={this.totalCheckedChange}/>
            </label>
            <span>
                <span>已完成{done}</span> / 全部
            </span>
            <button className="btn btn-danger">清除已完成任务</button>
         </div>
    )
  }
}
